mapper.update:
update film
title = #{title}, description = #{description}, release_year = #{releaseYear}, replacement_cost = #{replacementCost}, rating = #{rating}, last_update = #{lastUpdate}
where film_id = #{filmId}

mapper.insert:
insert into film
(title,description,release_year,replacement_cost,rating,last_update)
values (#{title},#{description},#{releaseYear},#{replacementCost},#{rating},#{lastUpdate});

    select last_insert_id() as film_id;
